while True:
    for event in pg.event.get():
        if event.type == pg.QUIT:
            quit()
            sys.exit(0)
        elif event.type == pg.MOUSEBUTTONDOWN:
            x_mouse, y_mouse = pg.mouse.get_pos()
            print(f'x={x_mouse} y={y_mouse}')
            column = x_mouse // (width+margin)
            row = y_mouse // (height+margin)
            if mas[row][column] == 0:
                mas[row][column] = 1
            else:
                mas[row][column] = 0
    for row in range(37):
        for col in range(37):
            x = col * width + margin * (col+1)
            y = row * width + margin * (row+1)
            if mas[row][col] == 1:
                color = blue
            else:
                color = white
            pg.draw.rect(screen, color, (x, y, width, height))
    pg.display.update()
    a = open('mas', 'w')
    a.write(str(mas))

